import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Link, Navigate } from 'react-router-dom';
import Home from './Pages/Home';
import Trivia1 from './Pages/Trivia1';
import Profile from './Pages/Profile';
import SignUp from './Pages/Auth';
import Signup2 from './Pages/Signup';

function App() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    // Log "signed out" when the component mounts and user is not signed in
    if (!user) {
      console.log("User signed out");
    }
  }, [user]);

  // const handleSignIn = (userData) => {
  //   if (userData && Object.keys(userData).length > 0) {
  //     setUser(userData);
  //     console.log("User logged in:", userData);
  //   } else {
  //     console.log("User not logged in");
  //   }
  // };

  const handleSignIn = (userData) => {
    setUser(userData);

    // Assume userData contains an 'id' property
    const userId = userData.id;

    // Navigate to the home route with the user ID in the state
    history.push('/home', { id: userId });
  };

  const handleSignOut = () => {
    // Perform any necessary cleanup or logout actions
    setUser(null);
    console.log("User logged out");
  };

  return (
    <Router>
      <div className='web-width-dt'>
        <nav className='head-nav'>
          <Link to="/home">
            <p className='logo'>&#9775;</p>
          </Link>
          <ul className='nav-links'>
            {user ? (
              <>
                <li>
                  <Link to="/events">Events</Link>
                </li>
                <li>
                  <Link to="/profile">Profile</Link>
                </li>
                <li onClick={handleSignOut}>Sign Out</li>
              </>
            ) : (
              <li>
                <Link to="/auth">Signup</Link>
              </li>
            )}
          </ul>
        </nav>

        <Routes>
          <Route path="/home" element={<Home />} />
          <Route
            path="/events"
            element={user ? <Trivia1 /> : <Navigate to="/auth" />}
          />
          <Route
            path="/profile"
            element={user ? <Profile /> : <Navigate to="/auth" />}
          />
          <Route path="/auth" element={<SignUp onSignIn={handleSignIn} />} />
          <Route path="/signup" element={<Signup2 />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
