import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const EventDetailPage = () => {
  const { eventId } = useParams();
  const [eventDetails, setEventDetails] = useState(null);

  useEffect(() => {
    const fetchEventDetails = async () => {
      try {
        const response = await fetch(`http://localhost:8000/api/getEvent/${eventId}`);
        const data = await response.json();
        setEventDetails(data);
      } catch (error) {
        console.error('Error fetching event details:', error);
      }
    };

    fetchEventDetails();
  }, [eventId]);

  return (
    <div>
      {eventDetails ? (
        <>
          <p>
            <strong>Event Name:</strong> {eventDetails.eventName},{' '}
            <strong>Location:</strong> {eventDetails.location},{' '}
            <strong>Start Date:</strong> {new Date(eventDetails.startDate).toLocaleString()},{' '}
            <strong>End Date:</strong>{' '}
            {eventDetails.endDate
              ? new Date(eventDetails.endDate).toLocaleString()
              : 'N/A'}
          </p>
          {/* Add more content based on the event data */}
        </>
      ) : (
        <p>Loading event details...</p>
      )}
    </div>
  );
};

export default EventDetailPage;
