import React, {useState, useEffect} from 'react';
import '../../src/App.css'
import { BrowserRouter as Router, Route, Routes, Link, useLocation } from 'react-router-dom';
import axios from 'axios';


const Calendar = () => {
    const [events, setEvents] = useState([]);

    useEffect(() => {
      const fetchData = async () => {
        try {
          const response = await fetch('http://localhost:8000/api/getCalendarEvents');
          const data = await response.json();
          setEvents(data.data);
        } catch (error) {
          console.error('Error fetching data:', error);
        }
      };

      fetchData();
    }, []);
    return (
        <div className='calendar-container'>
            <p className='calendar-text'>Calendars</p>
            <hr />

            <div>
                <div>
                    <p className='calendar-text'>Subscribed Calendars</p>
                </div>
                <div className='data-section'>
  {events.length > 0 ? (

    <ul className='events-section-2'>
      {events.map((event) => (
        <>

        <div>

          <div>
          <li className='event-lists' key={event._id}>
          <Link to={`/event/${event._id}`}>
            <div className='invited-section'>
              <div className='is1'>
              <strong>Event Name:</strong> {event.calendarName},{' '}
              <strong>Event Name:</strong> {event.calendarDescription},{' '}
              <strong>Event Name:</strong> {event.location},{' '}
            <br />




              </div>

              <div className='is2'>

              {event.pastedUrl ? (
  <img className='paste-img' src={event.pastedUrl} alt="Thumbnail" />
) : (
  <>
  <div className='no-img-btn'>
  <p>No image</p>
  </div>
  </>
)}
              {/* <p>You Are Invited!</p> */}
              </div>

            </div>
            </Link>
          </li>
          <hr className='line-comp' />
          </div>

            {/* <p> <strong>Start Date:</strong> {new Date(event.startDate).toLocaleString()},{' '}</p> */}
        </div>

        </>
      ))}
    </ul>


  ) : (
    <div className='inner-data-section'>
      <p className='noti-text'>
        No events found yet.. Either no events have been found or you need to create one...
      </p>
      <div className='main-cta'>
        <p>Create Event</p>
      </div>
    </div>
  )}
</div>
            </div>
                <Link to="/create-calendar" className='create-calendar-cta'>
            <div className='event-adding'>
                <p>Create +</p>
            </div>
                </Link>

        </div>
    )
}

export default Calendar;
