import React, { useState, useEffect }  from 'react';
import '../../src/App.css'
import Modal from './Modal';
import { BrowserRouter as Router, Route, Routes, Link, useNavigate } from 'react-router-dom';



const Trivia1 = () => {
    // const [showModal, setShowModal] = useState(true);

    // useEffect(() => {

    //   const timeoutId = setTimeout(() => {
    //     setShowModal(false);
    //   }, 30000);


    //   return () => clearTimeout(timeoutId);
    // }, []); 

    // const closeModal = () => {
    //   setShowModal(false);
    // };

    const [error, setError] = useState('');

    const history = useNavigate();

    const [eventName, setEventName] = useState('');
    const [startDate, setStartDate] = useState('');
    const [endDate, setEndDate] = useState('');
    const [location, setLocation] = useState('');

    const handleCreateEvent = () => {
      // Prepare the event data
      const eventData = {
        eventName,
        startDate,
        endDate,
        location,
      };

      // Make a POST request to your server
      fetch('http://localhost:8000/api/createEvent', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(eventData),
      })
        .then(response => response.json())
        .then(data => {
          // Handle the response from the server if needed
          console.log('Event created successfully:', data);
          console.log('passed');

          if (data.success) {
            history("/home");
          } else {
            console.log('Server response indicates failure');
          }
        })
        .catch(error => {
          console.error('Error creating event:', error);
          console.log('failed');
          setError('Error please try again....')
        });
    };
    return (
      <div className='events-section'>
      <div className='inner-left'>
        <input
          type='text'
          placeholder='Event Name'
          value={eventName}
          onChange={(e) => setEventName(e.target.value)}
        />

        <p>Start</p>
        <input
          type='datetime-local'
          value={startDate}
          onChange={(e) => setStartDate(e.target.value)}
        />

        <p>End</p>
        <input
          type='datetime-local'
          value={endDate}
          onChange={(e) => setEndDate(e.target.value)}
        />

        <p>Location</p>
        <input
          type='text'
          placeholder='Location'
          value={location}
          onChange={(e) => setLocation(e.target.value)}
        />

        <p>Theme</p>
        {/* Add more input fields as needed for other properties */}
        <p>{error}</p>
        <div className='create-btn' onClick={handleCreateEvent}>
          <p className='create-text'>Create Event</p>
        </div>
      </div>

      <div className='inner-right'>
        <div className='add-section'>
        <p>Add Section</p>
        </div>
      </div>
    </div>
    );
  };

  export default Trivia1;
